mapper.select:
SELECT t.*, getusername(t.create_user) createUserDesc, getusername(f.create_user) uploadUserDesc FROM t_biz_device t left join t_biz_files f on t.img_id = f.id where 1=1
and t.id = #{id}
and t.wiringdiagram_id = #{wiringdiagramId}
ORDER BY t.create_time desc

mapper.delete:
delete from t_biz_device 
	where id in 

	 #{item}
