mapper.select:
SELECT t.*, getusername(t.create_user) createUserDesc, getusername(f.create_user) uploadUserDesc, f.create_time uploadTime, f.path FROM t_biz_part_his t left join t_biz_files f on t.img_id = f.id where 1=1
and t.id = #{id}
and t.part_id = #{partId}
AND STR_TO_DATE(CONCAT(#{timeBegin}, ' 00:00:00'), '%Y-%m-%d %H:%i:%s') <= t.create_time
AND STR_TO_DATE(CONCAT(#{timeEnd}, ' 23:59:59'), '%Y-%m-%d %H:%i:%s') >= t.create_time
ORDER BY t.create_time desc

mapper.delete:
delete from t_biz_part_his 
	where id in 

	 #{item}
