mapper.select:
SELECT t.*, getusername(t.create_user) createUserDesc FROM t_biz_transformer t where 1=1
and t.state = #{state}
and t.id = #{id}
ORDER BY t.create_time desc

mapper.delete:
delete from t_biz_transformer 
	where id in 

	 #{item}

mapper.update:
update t_biz_transformer set
	state = #{state}
where id in 

 #{item}
