mapper.select:
SELECT t.*, getusername(t.create_user) createUserDesc, getusername(f.create_user) uploadUserDesc, f.create_time uploadTime, f.path FROM t_biz_wiringdiagram t left join t_biz_files f on t.img_id = f.id where 1=1
and t.id = #{id}
and t.transformer_id = #{transformerId}
ORDER BY t.create_time desc

mapper.delete:
delete from t_biz_wiringdiagram 
	where id in 

	 #{item}
